#' create_dataset
#'
#' Create new dataset in DDH
#'
#' @param credentials list: object returned by the get_credentials() function
#' @param body json: json object generated by jsonlite::toJSON
#' @param root_url character: API root URL
#'
#' @return list
#' @export
#'

create_dataset <- function(credentials, body, root_url = production_root_url) {
  cookie <- credentials$cookie
  token <- credentials$token

  # Build url
  path <- 'api/dataset/node'
  url <- httr::modify_url(root_url, path = path)
  # Send request
  out <- httr::POST(url = url,
                    httr::add_headers(.headers = c('Content-Type' = 'application/json',
                                                   'Cookie' =  cookie,
                                                   'X-CSRF-Token' = token)),
                    body = body,
                    encode = 'json')

  httr::warn_for_status(out)

  out <- httr::content(out)
  names(out) <- c('node_id', 'uri')

  return(out)
}
